export const ListModule = {
    state: () => ({
        lists: [],
        selectedSort: '',
        page:0,    
        limit:10,
        maxLists:0,
        sortOptions: [
            {value: 'title', name: 'По названию'},
            {value: 'body', name: 'По содержимому'}
        ],
        search: '',                                     
    // или так state: () => ({}) Без return
    }),
    getters: {
        sortedLists(state) {
            return [...state.lists].sort((list1,list2) =>  list1[state.selectedSort]?.localeCompare(list2[state.selectedSort]))
        },
        searchedAndSortLists(state,getters) {
            return getters.sortedLists.filter(list => list.title.toLowerCase().includes(state.search.toLowerCase()))
        },
    },
    mutations: {
        setLists(state,el) {
            state.lists = el
        },
        setSort(state,el) {
            state.selectedSort = el
        },
        setPage(state,el) {
            state.page = el
        },
        setMaxLists(state,el) {
            state.maxLists = el
        },
        setSearch(state,el) {
            state.search = el
        },

    },
    actions: {
        add(newList) {
            state.lists.push(newList);
        },
        remove(list) {
            commit(lists,state.lists.filter(l => l.id !== list.id))
        },
        showDialog() {
            commit(dialogVisible,true)
        },
        async fetchLists({state,commit}) {

            try {
                commit(page,+1)
                const response = await axios.get('https://jsonplaceholder.typicode.com/posts', {
                    params: {
                        _page: state.page,
                        _limit: state.limit,
                    }
                });
                commit(maxLists,Math.ceil(response.headers['x-total-count'] / state.limit))
                commit(lists,[...state.lists, ...response.data]) // удалить state.lists

            }catch {
                alert('пизда')
            }
            
        },
        changePage(pageNum,dispatch,commit) {
            commit(page,pageNum)
            dispatch.fetchLists()
        }
    },
    namespaced: true,
}
